<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the software license */ namespace Dtyq\SuperMagic\Interfaces\SuperAgent\DTO\Request; use App\Infrastructure\Core\AbstractDTO; use Hyperf\HttpServer\Contract\RequestInterface; /** * DeleteTopicRequestDTO * Used forReceiveDeleteTopicRequestParameter.*/ class DeleteTopicRequestDTO extends AbstractDTO { /** * TaskStatusID(Primary key) * StringType, Pair should TaskStatusTablePrimary key.*/ public string $id = ''; /** * getValidateRule.*/ public function rules(): array { return [ 'id' => 'required|string', ]; } /** * getValidateFailedCustomErrorinformation.*/ public function messages(): array { return [ 'id.required' => 'TaskStatusIDCannot be empty', 'id.string' => 'TaskStatusIDMust is String', ]; } /** * fromRequestCreate inDTOInstance.*/ public static function fromRequest(RequestInterface $request): self { $data = new self(); $data->id = $request->input('id', ''); return $data; } /** * getTaskStatusID(Primary key).*/ public function getId(): string { return $this->id; } } 