<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Dtyq\SuperMagic\Interfaces\SuperAgent\DTO\Request; use App\Infrastructure\Core\AbstractRequestDTO; use function Hyperf\Translation\__; /** * UpdateProjectPinStatusRequest DTO * Used forReceivePin/CancelPinProjectRequestParameter.*/ class UpdateProjectPinRequestDTO extends AbstractRequestDTO { /** * whetherPin: false-CancelPin, true-Pin.*/ public bool $isPin = false; /** * getwhetherPin.*/ public function getIsPin(): bool { return $this->isPin; } /** * setwhetherPin.*/ public function setIsPin(bool $isPin): void { $this->isPin = $isPin; } /** * CheckwhetherFor pin operation.*/ public function isPinOperation(): bool { return $this->isPin; } /** * Get validation rules. */ protected static function getHyperfValidationRules(): array { return [ 'is_pin' => 'required|boolean', ]; } /** * Get custom error messages for validation failures. */ protected static function getHyperfValidationMessage(): array { return [ 'is_pin.required' => __('project.pin.is_pin_required'), 'is_pin.boolean' => __('project.pin.is_pin_must_be_boolean'), ]; } } 