<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the software license */ namespace Dtyq\SuperMagic\Interfaces\SuperAgent\DTO\Request; use Hyperf\Validation\Request\FormRequest; class UpdateTaskStatusRequestDTO extends FormRequest { /** * @var string TaskID*/ protected string $taskId = ''; /** * @var string TaskStatus*/ protected string $status = ''; /** * ValidateRule.*/ public function rules(): array { return [ 'task_id' => 'required|string', 'status' => 'required|string|in:waiting, running, finished, error', ]; } /** * Property/AttributeName.*/ public function attributes(): array { return [ 'task_id' => 'TaskID', 'status' => 'TaskStatus', ]; } /** * getTaskID.*/ public function getTaskId(): string { return $this->taskId; } /** * getTaskStatus*/ public function getStatus(): string { return $this->status; } public function setTaskId(string $taskId): self { $this->taskId = $taskId; return $this; } public function setStatus(string $status): self { $this->status = $status; return $this; } /** * PrepareData.*/ protected function prepareForValidation(): void { $this->taskId = (string) $this->input('task_id', ''); $this->status = (string) $this->input('status', ''); } } 