<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Dtyq\SuperMagic\Interfaces\SuperAgent\DTO\Response; use Dtyq\SuperMagic\Domain\SuperAgent\Entity\TaskEntity; class CreateTaskResponseDTO { /** * @var string TaskID*/ protected string $taskId; /** * @var string TaskStatus*/ protected string $status; /** * @var string Created at*/ protected string $createdAt; /** * ConstructFunction.*/ public function __construct(int $taskId, string $status, string $createdAt) { $this->taskId = (string) $taskId; $this->status = $status; $this->createdAt = $createdAt; } /** * fromTaskEntityCreateResponseDTO.*/ public static function fromEntity(TaskEntity $entity): self { return new self( $entity->getId(), $entity->getStatus()->value, $entity->getCreatedAt() ); } /** * Convert toArray.*/ public function toArray(): array { return [ 'task_id' => $this->taskId, 'status' => $this->status, 'created_at' => $this->createdAt, ]; } /** * getTaskID.*/ public function getTaskId(): string { return $this->taskId; } /** * getTaskStatus*/ public function getStatus(): string { return $this->status; } /** * getCreated at.*/ public function getCreatedAt(): string { return $this->createdAt; } } 