<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Delightful\SuperMagic\Interfaces\SuperAgent\DTO\Response; use App\Infrastructure\Core\AbstractDTO; use Dtyq\SuperMagic\Domain\SuperAgent\Entity\TopicEntity; class TopicListResponseDTO extends AbstractDTO { /** * @var TopicItemDTO[] Topiclist*/ protected array $list = []; /** * @var int Total*/ protected int $total = 0; /** * fromEntitylistCreateResponse DTO.*/ public static function fromResult(array $result): self { $dto = new self(); $list = []; foreach ($result['list'] as $entity) { if ($entity instanceof TopicEntity) { $list[] = TopicItemDTO::fromEntity($entity); } } $dto->setList($list); $dto->setTotal($result['total']); return $dto; } public function getList(): array { return $this->list; } public function setList(array $list): self { $this->list = $list; return $this; } public function getTotal(): int { return $this->total; } public function setTotal(int $total): self { $this->total = $total; return $this; } } 