<?php declare(strict_types=1); /** * Copyright (c) Be Delightful , Distributed under the MIT software license */ namespace Dtyq\SuperMagic\Interfaces\SuperAgent\DTO\Response; use App\Infrastructure\Core\AbstractDTO; class TopicMetricsResponseDTO extends AbstractDTO { /** * @var StatusMetricsDTO StatusIndicatorData*/ protected StatusMetricsDTO $statusMetrics; /** * @var TotalMetricsDTO TotalIndicatorData*/ protected TotalMetricsDTO $totalMetrics; /** * getStatusIndicator.*/ public function getStatusMetrics(): StatusMetricsDTO { return $this->statusMetrics; } /** * setStatusIndicator.*/ public function setStatusMetrics(StatusMetricsDTO $statusMetrics): self { $this->statusMetrics = $statusMetrics; return $this; } /** * getTotalIndicator.*/ public function getTotalMetrics(): TotalMetricsDTO { return $this->totalMetrics; } /** * setTotalIndicator.*/ public function setTotalMetrics(TotalMetricsDTO $totalMetrics): self { $this->totalMetrics = $totalMetrics; return $this; } /** * Convert toArray.*/ public function toArray(): array { return [ 'status_metrics' => $this->statusMetrics->toArray(), 'total_metrics' => $this->totalMetrics->toArray(), ]; } } 